import React, { useState, useEffect } from "react";
import "./TaskList.css"; // Import external CSS

const TaskList = () => {
  // State to store tasks
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch tasks from the API (replace the URL with your real API endpoint)
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch("https://your-api-url.com/tasks");
        if (!response.ok) {
          throw new Error("Failed to fetch tasks");
        }
        const data = await response.json();
        setTasks(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchTasks();
  }, []); // Empty dependency array to run only once when the component mounts

  // Delete task handler
  const onDelete = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  // Toggle task completion handler
  const onToggleComplete = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Render loading or error message
  if (loading) return <p>Loading tasks...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="task-list-container">
      <h2 className="task-list-title">Your Tasks</h2>
      {tasks.length === 0 ? (
        <p className="no-tasks">No tasks available. Add some!</p>
      ) : (
        <ul className="task-list">
          {tasks.map((task) => (
            <li
              key={task.id}
              className={`task-item ${task.completed ? "completed" : ""}`}
            >
              <span
                className="task-text"
                onClick={() => onToggleComplete(task.id)}
              >
                {task.text}
              </span>
              <button
                className="delete-button"
                onClick={() => onDelete(task.id)}
              >
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
